/* Styling for the language selector */
.language-selector {
  position: absolute;
  top: 20px;
  right: 650px;
  display: flex;
  align-items: center;
  z-index: 10;
  cursor: pointer;
}

/* Globe icon styling */
.language-selector .globe-icon {
  width: 18px;
  height: 18px;
  margin-right: 8px; /* Adds space between the globe icon and the language text */
}

/* Selected language text styling */
.language-selector .selected-language {
  font-weight: 600;
  font-size: 16px;
  line-height: 26px;
  color: #454545;
  margin-right: 10px; /* Adds space between the language text and the arrow */
  text-align: left;
}

/* Styling for the dropdown select */
.language-dropdown {
  width: 120px;
  padding-right: 0;
  border: none;
  background: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="#454545" stroke-width="1.0" stroke-linecap="round" stroke-linejoin="round"><polyline points="6 9 12 15 18 9"></polyline></svg>') no-repeat;
  background-position: calc(100% - 3px) 65%;
  background-size: 20px 20px;
  font-family: 'Archia', sans-serif;
  font-size: 16px;
  font-weight: 600;
  line-height: 26px;
  gap: 10px;
  text-align: left;
  appearance: none;
  color: #454545;
  position: relative;
  border-radius: 20px;
  overflow: hidden;
  filter: url(#goo);
  /* Gooey effect */
}

.language-selector .globe-icon {
  width: 18px;
  height: 18px;
  margin-right: -1px; /* Space between globe icon and text */
}

/* Dropdown options styled as a "mine box" */
.language-dropdown option {
  background: #FFFFFF;
  color: #454545;
  padding: 8px 16px;
  border: none;
  border-radius: 8px;
  margin: 4px 0;
  box-shadow: 0px 2px 20px rgba(0, 0, 0, 0.1), 0px 8px 32px rgba(0, 0, 0, 0.04);
  transition: background-color 0.3s ease, color 0.3s ease;
}

/* Hover effect for dropdown options */
.language-dropdown option:hover {
  background: #007bff;
  color: #FFFFFF;
  box-shadow: 0px 8px 32px rgba(0, 0, 0, 0.15), 0px 4px 20px rgba(0, 0, 0, 0.1);
}

/* Custom arrow icon styling */
.language-dropdown+.arrow-icon {
  content: "";
  width: 10px;
  height: 10px;
  border-bottom: 2px solid #000;
  border-right: 2px solid #000;
  position: absolute;
  top: 50%;
  right: 5px; /* Adjust this value to move the arrow further to the right */
  transform: rotate(45deg);
  transform-origin: right;
  transition: transform 0.3s ease;
}

/* Rotate arrow when dropdown is open */
.language-selector.open .arrow-icon {
  transform: rotate(180deg); /* Rotate the arrow 180 degrees */
}
/* Hover effect for the dropdown itself */

  .language-selector .arrow-icon {
    position: absolute;
    top: 50%;
    right: -20px; /* Adjust spacing from the right */
    transform: translateY(-50%); /* Center vertically */
    width: 18px;
    height: 18px;
    transition: transform 0.3s ease;
  }
  

.language-dropdown:hover {
  filter: none;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.2);
  /* Soft shadow for hover effect */
}

/* Styling for the language selector container */

  .language-selector {
    
      position: absolute;
      top: 20px;
      right: 650px;
      display: flex;
      align-items: center;
      gap: 8px; /* Space between globe icon and text */
