/* Adjust spacing for the dropdown */
.language-selector select {
  margin-left: -35px;
}
.arrow-icon {
  width: 18px; /* Set the width to match the original icon */
  height: 18px; /* Set the height to match the original icon */
  transition: transform 0.3s ease; /* Smooth rotation transition */
}
