  opacity: 1;
  gap: 0px;
  display: block;
  transition: opacity 0.3s ease, transform 0.3s ease;
}

.language-selector.open + .language-dropdown-wrapper {
  opacity: 1;
  visibility: visible;
}

/* Dropdown options styling with margin, padding, and shadows */
.language-dropdown-wrapper .language-dropdown option {
  background: #FFFFFF; /* White background for options */
  color: #454545; /* Text color */
  padding: 12px 16px; /* Padding for the options */
  border: none; /* No border */
  border-radius: 8px; /* Rounded corners for individual options */
  margin: 4px 8px; /* Add slight spacing around options */
  transition: background-color 0.3s ease, color 0.3s ease;
  cursor: pointer;
}

.language-dropdown-wrapper .language-dropdown-option.selected {
  background: #e0eaff; /* Light blue for active state */
  color: #0a0a0a; /* Primary color for text */
  font-weight: normal; /* Emphasize the selected option */
}

/* Show dropdown when visible */
.language-selector.open + .language-dropdown-wrapper {
  opacity: 1;
  visibility: visible;
}

.language-dropdown-wrapper.open {
  transform: translateX(-50%) translateY(10px); /* Slight downward animation */
}

/* Hover effect for dropdown options */
.language-dropdown-wrapper .language-dropdown option:hover {
  background: #007bff;
  /* Blue background on hover */
  color: #ffffff;
  /* White text color on hover */
  box-shadow: 0px 8px 32px rgba(0, 0, 0, 0.15), 0px 4px 20px rgba(0, 0, 0, 0.1);
  /* Shadow on hover */
}

/* Dropdown visibility toggle */
.language-dropdown-wrapper {
  opacity: 1;
  display: block;
  z-index: 10;
}

/* Active option styling */
.active-option {
  background: #007bff;
  color: #fff;
  font-weight: bold;
}

/* Add transition for smoother experience */
.language-dropdown-option {
  background: #FFFFFF; /* White background for options */
  color: #000;
  padding: 12px 16px; /* Padding for the options */
  border: 0.1px #ddd; /* Light border for each option */
  border-radius: 8px; /* Rounded corners for options */
  margin: 4px 8px; /* Add slight spacing around options */
  transition: background-color 0.3s ease, color 0.3s ease; /* Smooth transition */
  cursor: pointer; /* Change cursor to pointer */
}

/* Hover effect remains intact */
.language-dropdown-option:hover {
  background-color: #f6f6f6;
  color: rgb(5, 5, 5);
}

/* Style for selected language option */
.language-dropdown-option.selected {
  background-color: #080808;
  color: #333;
  font-weight: bold;
}

.language-dropdown-option.hovered-option {
  background-color: #e5f1ff;
  color: #060606;
  box-shadow: 0px 8px 32px rgba(0, 0, 0, 0.15), 0px 4px 20px rgba(0, 0, 0, 0.1);
}

.language-selector:hover {
  background-color: #f9f9f9;
  border-color: #0f0f10;
}

.language-selector .arrow-icon {
  position: absolute;
  top: 50%;
  right: -15px; /* Adjust spacing from the right */
  transform: translateY(-50%); /* Center vertically */
  width: 18px;
  height: 18px;
  transition: transform 0.3s ease;
}

.language-selector.open .arrow-icon {
  transform: rotate(180deg);
}

@media (max-width: 768px) {
  .language-selector {
    padding: 8px 16px;
    font-size: 12px;
  }

  .language-dropdown-wrapper {
    width: 140px;
  }

  .language-dropdown-option {
    font-size: 12px;
    padding: 8px 12px;
  }
}

.language-selector .selected-language {
  font-weight: bold;
  /* Bold text for the selected language */
  color: #333;
}

/* General font styles */
.selected-language {
  font-weight: normal; /* Default font weight */
  font-size: 16px; /* Default font size */
  color: #454545;
  transition: font-weight 0.3s ease, font-size 0.3s ease; /* Smooth transition for font change */
}

.selected-language.selected {
  font-weight: bold; /* Bold font when selected */
  font-size: 18px; /* Larger font size when selected */
}

