/* Variables for Colors and Fonts */
$btn-color: #F04D1F;
$btn-hover-color: #D8431B;
$btn-disabled-color: #D8431B;
$btn-disabled-opacity: 0.7;
$input-focus-color: #2929F5;
$progress-bar-color: #4caf50;
$radio-checked-bg: #66b3ff;
$radio-checked-border: white;
$radio-focus-outline: #2929F5;

$font-family-main: 'Roundo', sans-serif;
$font-family-alt: 'Archia', sans-serif;

/* General Button Styling */
.btn {
  font-family: $font-family-main;
  border: none;
  border-radius: 8px;
  transition: all 0.3s ease;
  display: flex;
  justify-content: center;
  align-items: center;
  text-align: center;


/* Success Message Styling */
.success-message {
  background-color: #4caf50; /* Green background for success */
  color: white; /* White text */
  font-size: 16px; /* Font size */
  font-weight: 600; /* Bold text */
  padding: 12px 20px; /* Padding */
  border-radius: 8px; /* Rounded corners */
  width: auto; /* Allow the message to be as wide as necessary */
  max-width: 400px; /* Limit max width for the message */
  margin: 10px auto; /* Center the message */
  text-align: center; /* Center the text */
  display: block; /* Make it block-level for proper spacing */
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1); /* Light shadow for depth */
  transition: opacity 0.3s ease; /* Smooth transition for fading out */
}

/* Show the success message when triggered */
.success-message.show {
  opacity: 1; /* Ensure the message is visible */
}

/* Hide the success message */
.success-message.hide {
  opacity: 0; /* Hide the message */
  pointer-events: none; /* Disable interaction */
}

/* Success message hover effect */
.success-message:hover {
  background-color: #45a049; /* Darker green on hover */
}

  &:hover:not(.btn-disabled) {
    background-color: $btn-hover-color;
  }

